import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  constructor(private router: Router) { }

  navigateToProduct(): void {
    this.router.navigate(['/product']); 
  }
}
<div class="home-container">
  <header class="home-header">
    <h1>Bem-vindo à Aplicação</h1>
    <p>A sua solução para gerir tudo de forma eficiente.</p>
  </header>
  <main class="home-content">
    <div class="button-container">
      <button class="action-button" (click)="navigateToProduct()">Tela de Produtos</button>
      <button class="action-button" (click)="navigateToProduct()">Funcionalidade 1</button>
      <button class="action-button" (click)="navigateToProduct()">Funcionalidade 2</button>
      <button class="action-button" (click)="navigateToProduct()">Funcionalidade 3</button>
      <button class="action-button" (click)="navigateToProduct()">Funcionalidade 4</button>
      <button class="action-button" (click)="navigateToProduct()">Funcionalidade 5</button>
      <button class="action-button" (click)="navigateToProduct()">Funcionalidade 6</button>
      <button class="action-button" (click)="navigateToProduct()">Funcionalidade 7</button>
      <button class="action-button" (click)="navigateToProduct()">Funcionalidade 8</button>
      <button class="action-button" (click)="navigateToProduct()">Funcionalidade 9</button>
      <button class="action-button" (click)="navigateToProduct()">Funcionalidade 10</button>
    </div>
  </main>
</div>
