import { Injectable } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Observable } from 'rxjs';
import { Product } from '../models/product.model';


@Injectable({
  providedIn: 'root',
})
export class ProductService {
  private apiUrl = 'https://localhost:7221/api/Product';  // URL da API

  constructor(private http: HttpClient) { }

  getProducts(): Observable<Product[]> {  // A resposta deve ser um array de Product
    return this.http.get<Product[]>(this.apiUrl);  // Retorna um Observable do tipo Product[]
  }
}
