import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {
  constructor(private router: Router) { }

  navigateToProduct(): void {
    this.router.navigate(['/product']); 
  }
}
<div class="home-container">
  <header class="home-header">
    <h1>Bem-vindo à nossa Plataforma</h1>
    <p class="subheading">A solução completa para gestão eficiente de processos.</p>
  </header>
  <main class="home-content">
    <section class="intro-section">
      <p>Descubra como podemos otimizar o seu trabalho com ferramentas intuitivas e soluções inteligentes. Navegue por nossa plataforma para explorar todos os recursos disponíveis.</p>
      <button class="action-button" (click)="navigateToProduct()">Explorar Produtos</button>
    </section>
  </main>
  <footer class="home-footer">
    <p>&copy; 2025 Sua Empresa | Todos os direitos reservados</p>
  </footer>
</div>
